document.addEventListener(
  'DOMContentLoaded',
  function () {
    [].slice
      .call(document.querySelectorAll('[data-bss-tooltip]'))
      .map(function (e) {
        return new bootstrap.Tooltip(e);
      });
    var e = document.querySelectorAll('[data-bss-chart]');
    let sidebarT = document.getElementById('sidebarToggle');
    if (sidebarT == null) {
    } else sidebarT.click();
    for (var t of e) t.chart = new Chart(t, JSON.parse(t.dataset.bssChart));
  },
  !1
);
var p1 = document.getElementById('password'),
  p2 = document.getElementById('passwordRepeat'),
  alert = document.getElementById('alert'),
  submit = document.getElementById('submit');

if (!(p1 == null && p2 == null)) {
  p2.addEventListener('keyup', () => {
    p1.value != p2.value
      ? (alert.classList.remove('d-none'),
        (alert.innerHTML = 'Fjalekalimi nuk eshte i njejte'),
        (submit.disabled = !0))
      : (alert.classList.add('d-none'), (submit.disabled = !1));
  }),
    p1.addEventListener('keyup', () => {
      p1.value != p2.value
        ? (alert.classList.remove('d-none'),
          (alert.innerHTML = 'Fjalekalimi nuk eshte i njejte'),
          (submit.disabled = !0))
        : (alert.classList.add('d-none'), (submit.disabled = !1));
    });
}

(function () {
  'use strict';
  var e = document.querySelector('.sidebar'),
    t = document.querySelectorAll('#sidebarToggle, #sidebarToggleTop');
  if (e) {
    e.querySelector('.collapse');
    var n = [].slice
      .call(document.querySelectorAll('.sidebar .collapse'))
      .map(function (e) {
        return new bootstrap.Collapse(e, { toggle: !1 });
      });
    for (var l of t)
      l.addEventListener('click', function (t) {
        if (
          (document.body.classList.toggle('sidebar-toggled'),
          e.classList.toggle('toggled'),
          e.classList.contains('toggled'))
        )
          for (var l of n) l.hide();
      });
    window.addEventListener('resize', function () {
      if (
        Math.max(
          document.documentElement.clientWidth || 0,
          window.innerWidth || 0
        ) < 768
      )
        for (var e of n) e.hide();
    });
  }
  var o = document.querySelector('body.fixed-nav .sidebar');
  o &&
    o.on('mousewheel DOMMouseScroll wheel', function (e) {
      if (
        Math.max(
          document.documentElement.clientWidth || 0,
          window.innerWidth || 0
        ) > 768
      ) {
        var t = e.originalEvent,
          n = t.wheelDelta || -t.detail;
        (this.scrollTop += 30 * (n < 0 ? 1 : -1)), e.preventDefault();
      }
    });
  var a = document.querySelector('.scroll-to-top');
  a &&
    window.addEventListener('scroll', function () {
      var e = window.pageYOffset;
      a.style.display = e > 100 ? 'block' : 'none';
    });
})();
